num1 = float(input("digite um número: "))                                                                                                           
num2 = float(input("digite outro número: "))                                                                                                        
escolha = input("escolha a operação (+, -, *, /): ")                                                                                                
                                                                                                                                                    
if escolha == "+":                                                                                                                                  
        print("resultado: ", num1 + num2)                                                                                                           
elif escolha == "-":                                                                                                                                
        print("resultado: ", num1 - num2)                                                                                                           
elif escolha == "*":                                                                                                                                
        print("resultado: ", num1 * num2)                                                                                                           
else:                                                                                                                                               
        print("resultado: ", num1 / num2)                                                                                                           
                                           